mapper.insert:
insert into tb_board(
  title
  , contents
  , write_id
  , created_id
  , created_date
  , updated_id
  , updated_date
) VALUES (
  #{title}
  , #{contents}
  , #{writeId}
  , #{createdId}
  , #{createdDate}
  , #{updatedId}
  , #{updatedDate}
)

mapper.update:
update tb_board tb
set
  tb.title = #{board.title}
  , tb.contents = #{board.contents}
  , tb.updated_id = #{board.updatedId}
  , tb.updated_date = #{board.updatedDate}
where tb.seq = #{seq}
