mapper.insert:
insert into tb_member(
  email
  , name
  , passwd
  , login_count
  , created_date
) VALUES (
  #{email}
  , #{name}
  , #{passwd}
  , 0
  , current_timestamp(6)
)

mapper.update:
update tb_member tm
set
  tm.email = #{member.email}
  , tm.name = #{member.name}
  , tm.passwd = #{member.passwd}
where tm.seq = #{seq}
